import React, { useState } from 'react';
import PowerCard from './PowerCard';
import { Container, Typography, Grid } from '@mui/material';

const Dashboard = () => {

const[powerData,setPowerData]= useState({
  voltage: 0,
  current: 0,
  temperature: 0,
  pressure: 0,
});


  return (
    <Container>
      <Typography variant="h2" color="green" gutterBottom>
        Power Plant Monitoring Dashboard
      </Typography>

      <Grid container spacing={3}>
        <PowerCard title="Voltage" value={powerData.voltage} />
        <PowerCard title="Current" value={powerData.current} />
        <PowerCard title="Temperature" value={powerData.temperature} />
        <PowerCard title="Pressure" value={powerData.pressure} />
      </Grid>

    </Container>
  )
}

export default Dashboard;
